import React from 'react';
import ReactDOM from 'react-dom/client';
import './index.css';

const root = ReactDOM.createRoot(document.getElementById("root"));

const value = "Мои увлечения";
const firstName = "Марина";
const lastName = "В.";
const items = ["Музыка", "Кино", "Книги", "Диван"]

const date = new Date().getFullYear();

root.render(
  <div>
    <h1>{value}</h1>
    <h2>
      {firstName} {lastName}
    </h2>
    <ul>
      {
        // Если есть массив, то:
      // items.map(item => (<li>{item}</li>))

      // Если нет массива:
        Array.from({length: 5}, (_, index) => <li>{`Элемент ${index + 1}`}</li>)
      }
    </ul>
    <p>
      <span dangerouslySetInnerHTML={{ __html: "&copy;" }} /> Фабрики - рабочим,
      земля - крестьянам, пиво - мне!
    </p>
    <p>{date} год</p>
  </div>
);


